import React, { useState, useEffect } from 'react';

export default function Bored() {

    const [bored, setBored] = useState([])
    const fetchBored = () => {
        fetch('http://www.boredapi.com/api/activity?type=recreational')
            .then(res => res.json())
            .then(res => setBored(res))
            console.log(bored)
    };
    useEffect(() => {
        fetchBored()
    },[]);
        


    return (
        <>
            <div style={{ marginTop: '30px' }} className='container'>
                <div className='text-center siteText text-decoration-underline mb-5'>Do a thing?</div>
                <div className='nameText row'>
                    <div className='col-md-4'>
                        As an avid doer of things, I am an advocate for doing things! 
                        Here's a fun little button to help you get started on your next, new thing!
                    </div>
                    <button className='btn btn-lg btt link-dark col-md-4' onClick={fetchBored}>Your new thing awaits!</button>
                    <div className='col-md-3 card'>
                        {bored.activity}
                    </div>
                </div>
            </div>
        </>
    );
};
