import React, { useState, useEffect } from 'react'

export default function Bored() {

    const [bored, setBored] = useState([])

    const fetchBored = () => {
        fetch('http://www.boredapi.com/api/activity?type=recreational')
            .then(res => res.json())
            .then(res => setBored(res))
    }

    useEffect(() => {
        fetchBored()
    }, [])
        


    return (
        <>
            <div className='container'>
                <div className='text-center siteText text-decoration-underline'>Do a thing?</div>
                <div className='nameText row'>
                    <div className='col-md-6'>
                        Hello
                    </div>
                    <div className='col-md-3'>
                        Bitches
                    </div>
                    <button className='btn btn-primary col-md-3' onClick={fetchBored}>Bored</button>
                    <div>
                        {bored?.map()}
                    </div>
                </div>
            </div>
        </>
    )
}
