import Typewriter from 'typewriter-effect';
import FinishedProjects from './FinishedProjects.js'
// import { useRef } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Element } from 'react-scroll';



export default function MainPage() {
    // const finishedProjects = useRef(null)
    // const fpScroll = () => {
    //     finishedProjects.current?.scrollIntoView({ behavior: 'smooth' });
    // };
    return (
        <>
            {/* <div className='px-4 py-5 my-5 text-center'> */}
            <div className='container'>
                <div className='row'>
                    <div className='me-5 col'>
                        <img style={{ borderRadius: '50%' }} className='image-fluid ms-5 b_pic' width='100%' src='./bun1.jpg' alt='no bun :(' />
                    </div>
                    <div style={{ marginLeft: '150px' }} className='col'>
                        <h1 className='display-1 nameText csep'>Welcome!</h1>
                        <div className='fs-2 siteText'>
                            <Typewriter
                                onInit={(typewriter) => {
                                    typewriter
                                        .typeString('My name is Adrian Dorado, avid doer of things.')
                                        .pauseFor(1000)
                                        .deleteAll()
                                        .typeString('Contact me @ doradocoding@gmail.com')
                                        .pauseFor(1000)
                                        .deleteAll()
                                        .typeString('Contact me @ doradocoding@gmail.com')
                                        .pauseFor(1000)
                                        .deleteAll()
                                        .typeString('I hope you enjoy your stay <3')
                                        .start()
                                }}
                            />
                        </div>
                        <hr />
                        <div className='mt-3'>
                            <h2 className='nameText'>What's the hops?</h2>
                            <p className='siteText'>
                            Hey, hey, everyone! Welcome to my site! <br/> 
                            Here you'll find my finished projects along with some links to my <br/> 
                            deployed projects and my project repos!<br/>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
            <Element id='finished-projects'>
                <FinishedProjects />
            </Element>
        </>
    );
}
